import React, { useEffect, useRef } from 'react';
import NavBar from '../components/Navbar/NavBar';
import { Link } from 'react-router-dom';

// Define the loadScript function here
function loadScript(src, id) {
  return new Promise((resolve, reject) => {
    if (document.getElementById(id)) {
      resolve();
      return;
    }
    const script = document.createElement('script');
    script.src = src;
    script.id = id;
    script.onload = () => resolve();
    script.onerror = () => reject(new Error(`Failed to load the script ${src}`));
    document.head.appendChild(script);
  });
}

const Hero = () => {
  const heroRef = useRef(null);

  useEffect(() => {
    const initVanta = async () => {
      await loadScript('https://cdn.jsdelivr.net/npm/three@0.134.0/build/three.min.js', 'three-script');
      await loadScript('https://www.vantajs.com/dist/vanta.globe.min.js', 'vanta-script');

      // Assuming VANTA is now available globally, initialize it
      if (window.VANTA) {
        window.VANTA.GLOBE({
          el: heroRef.current,
          mouseControls: true,
          touchControls: true,
          gyroControls: false,
          minHeight: 500.00, // Adjust the height as needed
          minWidth: 200.00,
          scale: 1.00,
          scaleMobile: 1.00,
          color: 0xf3096,
          backgroundColor: 0xffffff
        });
      }
    };

    initVanta();

    // Cleanup function to destroy Vanta effect when the component unmounts
    return () => {
      if (window.VANTA) {
        if (window.VANTA.animations) {
          window.VANTA.animations.forEach(anim => anim.destroy());
          window.VANTA.animations = [];
        }
      }
    };
  }, []);

  return (
    <>
        <nav className={`navBar fixed top-0 left-0 w-full z-50 transition duration-300 ease-in-out ${!top && 'bg-white shadow-lg'}`}>
            <div className="flex flex-row justify-between items-center py-2">
                <div className="flex flex-row justify-center md:px-12 md:mx-12 items-center text-center font-semibold">
                    <HashLink smooth to="/#hero"><h1 className="font-extrabold text-4xl text-blue-900">Prime Distributions</h1></HashLink>
                </div>
                <div className="group flex flex-col items-center">
                    <button className="p-2 rounded-lg lg:hidden text-blue-900" onClick={handleClick}>
                        <svg className="h-6 w-6 fill-current" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
                            {isOpen ? (
                                <path fillRule="evenodd" clipRule="evenodd" d="M18.278 16.864a1 1 0 0 1-1.414 1.414l-4.829-4.828-4.828 4.828a1 1 0 0 1-1.414-1.414l4.828-4.829-4.828-4.828a1 1 0 0 1 1.414-1.414l4.829 4.828 4.828-4.828z" />
                            ) : (
                                <path fillRule="evenodd" d="M4 5h16a1 1 0 0 1 0 2H4a1 1 0 1 1 0-2zm0 6h16a1 1 0 0 1 0 2H4a1 1 0 0 1 0-2zm0 6h16a1 1 0 0 1 0 2H4a1 1 0 0 1 0-2z" />
                            )}
                        </svg>
                    </button>
                    <div className={`lg:flex flex-row space-x-6 p-5 ${isOpen ? 'fixed inset-0 bg-white z-50' : 'hidden'}`}>
                        <NavLinks />
                    </div>
                </div>
            </div>
        </nav>
        <div className="hero" id='hero'>
            <div className="m-auto overflow-hidden mx-4 mt-8 lg:mt-4 p-2 md:p-12 h-screen md:h-98"> {/* Set the height of hero section as needed */}
                <div className="flex flex-col lg:flex-row py-8 justify-between text-center lg:text-left">
                    <div className="lg:w-1/2 flex flex-col justify-center" data-aos="zoom-in" data-aos-delay="200">
                        <h1 className="mb-5 md:text-5xl text-3xl font-bold">
                            <span className="gradient-empowering">Empowering</span> Your Business Through Strategic <span className="gradient-distribution">Wholesale Distribution</span>
                        </h1>
                        <div className="text-xl font-semibold tracking-tight mb-5 text-gray-500">
                            We are a team of highly motivated and skilled developers dedicated to delivering only the best software.
                        </div>
                        <div className="mb-4 space-x-0 md:space-x-2 md:mb-8">
                            <Link to="/contact" className="text-white bg-blue-900 hover:bg-blue-800 inline-flex items-center justify-center w-full px-6 py-3 my-4 text-lg shadow-xl rounded-2xl sm:w-auto sm:mb-0">
                                Learn more
                                <svg className="w-4 h-4 ml-1" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                                    <path fillRule="evenodd" d="M10.293 3.293a1 1 0 011.414 0l6 6a1 1 0 010 1.414l-6 6a1 1 0 01-1.414-1.414L14.586 11H3a1 1 0 110-2h11.586l-4.293-4.293a1 1 0 010-1.414z" clipRule="evenodd"></path>
                                </svg>
                            </Link>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </>
);
};

export default Hero;
