import React, { useState, useEffect } from 'react';
import NavLinks from '../Navbar/NavLinks';
import { HashLink } from 'react-router-hash-link';
import { Link } from 'react-router-dom';

const NavBar = () => {
    const [top, setTop] = useState(true);
    const [isOpen, setisOpen] = useState(false);

    function handleClick() {
        setisOpen(!isOpen);
    }

    useEffect(() => {
        const scrollHandler = () => {
            window.pageYOffset > 10 ? setTop(false) : setTop(true);
        };
        window.addEventListener('scroll', scrollHandler);
        return () => window.removeEventListener('scroll', scrollHandler);
    }, [top]);
    return (
        <div className="hero" ref={heroRef} id="hero">
          <NavBar />
          <div className="m-auto overflow-hidden mx-4 mt-20 lg:mt-12 p-2 md:p-12 h-screen md:h-98 flex items-center justify-center">
            <div className="flex flex-col lg:flex-row py-8 justify-between text-center lg:text-left">
              <div className="lg:w-1/2 flex flex-col justify-center" data-aos="zoom-in" data-aos-delay="200">
                <h1 className="mb-5 md:text-5xl text-3xl font-bold">
                  <span className="gradient-empowering">Empowering</span> Your Business Through Strategic{' '}
                  <span className="gradient-distribution">Wholesale Distribution</span>
                </h1>
                <div className="text-xl font-semibold tracking-tight mb-5 text-gray-500">
                  We are a team of highly motivated and skilled developers dedicated to delivering only the best software.
                </div>
                <div className="mb-4 space-x-0 md:space-x-2 md:mb-8">
                  <Link
                    to="/contact"
                    className="text-white bg-blue-900 hover:bg-blue-800 inline-flex items-center justify-center w-full px-6 py-3 my-4 text-lg shadow-xl rounded-2xl sm:w-auto sm:mb-0"
                  >
                    Learn more
                    <svg className="w-4 h-4 ml-1" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                      <path
                        fillRule="evenodd"
                        d="M10.293 3.293a1 1 0 011.414 0l6 6a1 1 0 010 1.414l-6 6a1 1 0 01-1.414-1.414L14.586 11H3a1 1 0 110-2h11.586l-4.293-4.293a1 1 0 010-1.414z"
                        clipRule="evenodd"
                      ></path>
                    </svg>
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      );
}

export default NavBar;
