import React, { useEffect, useRef } from 'react';
import NavBar from '../components/Navbar/NavBar';
import heroImg from '../images/web-dev.svg';

const Hero = () => {
  const heroRef = useRef(null);

  useEffect(() => {
    const initVanta = async () => {
      await loadScript('https://cdn.jsdelivr.net/npm/three@0.134.0/build/three.min.js', 'three-script');
      await loadScript('https://www.vantajs.com/dist/vanta.globe.min.js', 'vanta-script');

      // Initialize VANTA.GLOBE with your element
      window.VANTA.GLOBE({
        el: heroRef.current,
        mouseControls: true,
        touchControls: true,
        gyroControls: false,
        minHeight: 200.00,
        minWidth: 200.00,
        scale: 1.00,
        scaleMobile: 1.00,
        color: 0xffffff,
        color2: 0xe3e3e3,
        backgroundColor: 0xbb6300,
        size: 1.00
      });
    };

    initVanta();

    // Cleanup Vanta.js effect on component unmount
    return () => {
      if (window.VANTA) {
        window.VANTA.animations.forEach(anim => anim.destroy());
        window.VANTA.animations = [];
      }
    };
  }, []);

  return (
    <>
      <div className="hero" ref={heroRef} id='hero'>
        <div>
          <NavBar />
        </div>

        {/* Your existing JSX content */}
        <div className="m-auto overflow-hidden mx-4 mt-8 lg:mt-4 p-2 md:p-12 h-5/6" data-aos="zoom-in">
          {/* Existing content */}
        </div>
      </div>
    </>
  );
};

export default Hero;
