import React from 'react';
import img from '../images/support.svg';
import { Link } from 'react-router-dom';
import '.,,/index.css'; // Assuming you have an external CSS file for styles

const Products = () => {
    return (
        <>
            <style>
                {`
                    @keyframes fadeIn {
                        from {
                            opacity: 0;
                        }
                        to {
                            opacity: 1;
                        }
                    }
                    .fadeIn {
                        animation: fadeIn ease 1s;
                        animation-fill-mode: forwards;
                    }
                `}
            </style>
            <div className="m-auto max-w-6xl p-2 md:p-12 h-5/6 fadeIn" id='about'>
                <div className="flex flex-col-reverse lg:flex-row py-8 justify-between lg:text-left fadeIn" data-aos="fade-up">
                    <div className="flex-col my-4 text-center lg:text-left lg:my-0 lg:justify-end w-full lg:w-1/2 px-8 fadeIn" data-aos="zoom-in" data-aos-delay="500">
                        <h3 className="text-3xl text-blue-900 font-bold">
                            <span className="gradient-empowering">Exceeding</span> Expectations Through
                            <span className="gradient-distribution"> Personalized Support</span>
                        </h3>
                        <div>
                            <p className='my-3 text-xl text-gray-600 font-semibold'>
                                At Prime Distribution, we believe in setting the standard for unparalleled service. Our dedicated team is passionately committed to providing you with an exceptional experience tailored to your unique needs. Whether you're a burgeoning startup or an established retailer, we go above and beyond to ensure your satisfaction.
                            </p>
                        </div>

                        <div>
                            <p className='my-3 text-xl text-gray-600 font-semibold'>
                                From prompt and friendly assistance to personalized product recommendations, every interaction with Prime Distribution is designed to exceed your expectations. With us, you're not just a client; you're a valued partner on the path to success.
                            </p>
                        </div>
                        <Link to="/contact" className="text-white bg-blue-900 hover:bg-blue-800 inline-flex items-center justify-center w-full px-6 py-2 my-4 text-lg shadow-xl rounded-2xl sm:w-auto sm:mb-0 group">
                            Contact us
                            <svg className="w-4 h-4 ml-1 group-hover:translate-x-2" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                                <path fillRule="evenodd" d="M10.293 3.293a1 1 0 011.414 0l6 6a1 1 0 010 1.414l-6 6a1 1 0 01-1.414-1.414L14.586 11H3a1 1 0 110-2h11.586l-4.293-4.293a1 1 0 010-1.414z" clipRule="evenodd"></path>
                            </svg>
                        </Link>
                    </div>
                    <div className="lg:w-1/2 flex flex-col lg:mx-4 justify-center fadeIn">
                        <img alt="card img" className="rounded-t float-right" src={img} />
                    </div>
                </div>
            </div>
        </>
    );
}

export default Products;
