import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import VANTA from 'vanta/dist/vanta.globe.min.js';

const Hero = () => {
    const heroRef = useRef(null);

    useEffect(() => {
        // Initialize Vanta.js with the `.hero` ref and your custom settings
        const vantaEffect = VANTA.GLOBE({
            el: heroRef.current,
            THREE: THREE, // Pass the THREE instance
            mouseControls: true,
            touchControls: true,
            gyroControls: false,
            minHeight: 200.00,
            minWidth: 200.00,
            scale: 1.00,
            scaleMobile: 1.00,
            color: 0xffffff,
            color2: 0xe3e3e3,
            backgroundColor: 0xbb6300
        });

        return () => {
            if (vantaEffect) vantaEffect.destroy();
        };
    }, []); // The empty array ensures this effect runs only once when the component mounts

    return (
        <>
            <div className="hero" ref={heroRef} id='hero'>
                {/* Your existing JSX content */}
            </div>
        </>
    );
};

export default Hero;
