import React from 'react';
import productSourcingImg from '../images/product-sourcing.svg';
import competitivePricingImg from '../images/competitive-pricing.svg';
import customerServiceImg from '../images/customer-service.svg';
import supplierRelationshipImg from '../images/supplier-relationship.svg';

const Clients = () => {
    const Clients = [
        {
            id: 1,
            title: 'Product Sourcing',
            description: 'We identify trusted wholesalers and negotiate terms to acquire high-quality products. With a focus on reliability and quality assurance, we ensure that our sourcing partners meet stringent standards.',
            imageUrl: productSourcingImg,
        },
        {
            id: 2,
            title: 'Competitive Pricing',
            description: 'We source top-quality products from wholesalers worldwide and provide competitive prices for our customers, ensuring you get the best value for your investments.',
            imageUrl: competitivePricingImg,
        },
        {
            id: 3,
            title: 'Customer Service',
            description: 'We provide responsive customer support to address inquiries, resolve issues, and ensure customer satisfaction with every interaction.',
            imageUrl: customerServiceImg,
        },
        {
            id: 4,
            title: 'Supplier Relationship',
            description: 'Building and maintaining strong relationships with wholesalers, fostering open communication, and collaborating on joint initiatives to drive mutual business success.',
            imageUrl: supplierRelationshipImg,
        },
    ];

    return (
        <>
            <div className="m-auto max-w-6xl p-2 md:p-12">
                <div className="grid md:grid-cols-2 gap-8">
                    {Clients.map(service => (
                        <div key={service.id} className="bg-white rounded-xl shadow-lg hover:shadow-xl transition-shadow duration-300 ease-in-out">
                            <div className="p-6">
                                <img src={service.imageUrl} alt={service.title} className="w-full h-40 object-cover rounded-t-lg mb-4" />
                                <h5 className="text-xl font-bold mb-2">{service.title}</h5>
                                <p className="text-gray-700">{service.description}</p>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </>
    );
}

export default Clients;
