import React, { useEffect, useRef } from 'react';
import NavBar from '../components/Navbar/NavBar';
import { Link } from 'react-router-dom';

function loadScript(src, id) {
  return new Promise((resolve, reject) => {
    if (document.getElementById(id)) {
      resolve();
      return;
    }
    const script = document.createElement('script');
    script.src = src;
    script.id = id;
    script.onload = () => resolve();
    script.onerror = () => reject(new Error(`Failed to load the script ${src}`));
    document.head.appendChild(script);
  });
}

const Hero = () => {
  const heroRef = useRef(null);

  useEffect(() => {
    const initVanta = async () => {
      await loadScript('https://cdn.jsdelivr.net/npm/three@0.134.0/build/three.min.js', 'three-script');
      await loadScript('https://www.vantajs.com/dist/vanta.globe.min.js', 'vanta-script');

      if (window.VANTA) {
        window.VANTA.GLOBE({
          el: heroRef.current,
          mouseControls: true,
          touchControls: true,
          gyroControls: false,
          minHeight: 400.00, // Adjusted minHeight to ensure container visibility
          minWidth: 200.00,
          scale: 1.00,
          scaleMobile: 1.00,
          color: 0xf3096,
          backgroundColor: 0xffffff
        });
      }
    };

    initVanta();

    return () => {
      if (window.VANTA && window.VANTA.animations) {
        window.VANTA.animations.forEach(anim => anim.destroy());
        window.VANTA.animations = [];
      }
    };
  }, []);

  return (
    <>
      <div className="hero" ref={heroRef} id='hero' style={{ minHeight: '75vh' }}> {/* Ensure minHeight for the hero section */}
        <div>
          <NavBar />
        </div>

        <div className="m-auto overflow-hidden mx-4 mt-8 lg:mt-4 p-2 md:p-12" data-aos="zoom-in">
          <div className="flex flex-col justify-center items-center text-center h-full">
            <h1 className="mb-5 md:text-5xl text-3xl font-bold">
              <span className="gradient-empowering">Empowering</span> Your Business Through Strategic <span className="gradient-distribution">Wholesale Distribution</span>
            </h1>
            <div className="text-xl font-semibold tracking-tight mb-5 text-gray-500">
              We are a team of highly motivated and skilled professionals dedicated to delivering the best results.
            </div>
            <Link to="/contact" className="text-white bg-blue-900 hover:bg-blue-800 inline-flex items-center justify-center px-6 py-3 my-4 text-lg shadow-xl rounded-2xl">
              Learn more
              <svg className="w-4 h-4 ml-1" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M10.293 3.293a1 1 0 011.414 0l6 6a1 1 0 010 1.414l-6 6a1 1 0 01-1.414-1.414L14.586 11H3a1 1 0 110-2h11.586l-4.293-4.293a1 1 0 010-1.414z" clipRule="evenodd"></path>
              </svg>
            </Link>
          </div>
        </div>
      </div>
    </>
  );
};

export default Hero;
